<template><div><h1 class='text-2xl font-bold mb-4'>ADMIN — Vježbe</h1><form @submit.prevent='save' class='bg-white border rounded-2xl p-4 space-y-3'><input v-model='name' placeholder='Naziv' required class='input'/><select v-model='body_part' required class='input'><option v-for='bp in bodyParts' :key='bp' :value='bp'>{{ bp }}</option></select><input v-model='youtube_url' placeholder='YouTube URL' class='input'/><input v-model='short_desc' placeholder='Kratki opis' class='input'/><textarea v-model='how_to' placeholder='Upute izvođenja' class='input'></textarea><button class='btn'>Spremi</button></form><ul class='mt-6 space-y-2'><li v-for='e in items' :key='e.id' class='bg-white border rounded-2xl p-4 flex justify-between items-center'><div><b>{{ e.name }}</b> ({{ e.body_part }}) <span v-if='e.youtube_url'>— <a :href='e.youtube_url' target='_blank'>video</a></span></div><button @click='removeItem(e.id)' class='text-red-600 hover:underline'>Obriši</button></li></ul></div></template><script setup>import { ref, onMounted } from 'vue';import http from '../api/http';const bodyParts=['PRSA','LEDJA','NOGE','BICEPS','TRICEPS','RAMENA'];const name=ref('');const body_part=ref('PRSA');const youtube_url=ref('');const short_desc=ref('');const how_to=ref('');const items=ref([]);async function load(){ const { data } = await http.get('/exercises'); items.value=data;}async function save(){ await http.post('/exercises', { name:name.value, body_part:body_part.value, youtube_url:youtube_url.value, short_desc:short_desc.value, how_to:how_to.value }); name.value = youtube_url.value = short_desc.value = how_to.value = ''; body_part.value='PRSA'; await load(); }async function removeItem(id){ await http.delete(`/exercises/${id}`); await load(); }onMounted(load);</script><style scoped>.input{ @apply w-full border rounded-xl px-3 py-2 focus:outline-none focus:ring; } .btn{ @apply bg-blue-600 text-white rounded-xl px-4 py-2 hover:bg-blue-700; }</style>