<template><div><h1 class='text-3xl font-bold mb-6'>Vje≈æbe</h1><div class='mb-6'><label class='block text-gray-700 mb-2'>Filtriraj po dijelu tijela:</label><select v-model='filter' @change='load' class='border rounded-xl px-4 py-2 w-60'><option value=''>Sve</option><option v-for='bp in bodyParts' :key='bp' :value='bp'>{{ bp }}</option></select></div><div class='grid md:grid-cols-2 lg:grid-cols-3 gap-6'><ExerciseCard v-for='e in items' :key='e.id' :exercise='e' /></div></div></template><script setup>import { ref, onMounted } from 'vue';import http from '../api/http';import ExerciseCard from '../components/ExerciseCard.vue';const bodyParts=['PRSA','LEDJA','NOGE','BICEPS','TRICEPS','RAMENA'];const filter=ref('');const items=ref([]);async function load(){ const url=filter.value?`/exercises?body_part=${filter.value}`:'/exercises'; const { data } = await http.get(url); items.value=data;}onMounted(load);</script>