<template><div class='max-w-md mx-auto bg-white rounded-2xl shadow p-6'><h1 class='mb-4'>Prijava</h1><form @submit.prevent='onLogin' class='space-y-3'><input v-model='email' type='email' placeholder='Email' required class='input'/><input v-model='password' type='password' placeholder='Lozinka' required class='input'/><button class='btn w-full'>Prijavi se</button></form></div></template><script setup>import { ref } from 'vue';import { useRouter } from 'vue-router';import { useAuth } from '../stores/auth';const email=ref('');const password=ref('');const router=useRouter();const auth=useAuth();async function onLogin(){ await auth.login(email.value, password.value); router.push('/'); }</script><style scoped>.input{ @apply w-full border rounded-xl px-3 py-2 focus:outline-none focus:ring; } .btn{ @apply bg-blue-600 text-white rounded-xl px-4 py-2 hover:bg-blue-700; }</style>