<template><div class='max-w-md mx-auto bg-white rounded-2xl shadow p-6'><h1 class='mb-4'>Registracija</h1><form @submit.prevent='onRegister' class='space-y-3'><input v-model='email' type='email' placeholder='Email' required class='input'/><input v-model='username' placeholder='Korisničko ime' required class='input'/><input v-model='password' type='password' placeholder='Lozinka' required class='input'/><button class='btn w-full'>Kreiraj račun</button></form></div></template><script setup>import { ref } from 'vue';import { useRouter } from 'vue-router';import http from '../api/http';const email=ref('');const username=ref('');const password=ref('');const router=useRouter();async function onRegister(){ await http.post('/auth/register', { email:email.value, username:username.value, password:password.value }); router.push('/login'); }</script><style scoped>.input{ @apply w-full border rounded-xl px-3 py-2 focus:outline-none focus:ring; } .btn{ @apply bg-green-600 text-white rounded-xl px-4 py-2 hover:bg-green-700; }</style>